import React,{Component} from 'react';
import {Form, Row, ControlLabel, FormGroup,FormControl, Button} from 'react-bootstrap';
import './FormApp';
import axios from 'axios';


class FormApp extends Component{
    constructor(props){
        super(props);
        this.state={
            name:'',
            company:'',
            email:'',
            phone:'',
            message:'',
            sent: false,
            buttonText:'send Message'
        };
        this.onNameChange = this.onNameChange.bind(this);
        this.onCompanyChange=this.onCompanyChange.bind(this);
        this.onEmailChange = this.onEmailChange.bind(this);
        this.onPhoneCange=this.onPhoneChange.bind(this);
        this.onMessageChange = this.onMessageChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
    }
    onNameChange(event){
      this.setState({name:event.target.value})
      }
    onCompanyChange(event){
        this.setState({company:event.target.value})
        }
    onEmailChange(event){
         this.setState({email: event.target.value})
       }
       onPhoneChange(event){
        this.setState({phone:event.target.value})
        }
       onMessageChange(event){
          this.setState({message: event.target.value})
    }
     handleSubmit(event) {
        event.preventDefault();



      axios({
        methode:"POST",
        url:'http://localhost:3000/send',
        data:{
          name:this.state.name,
          company:this.state.company,
          email:this.state.email,
          phone:this.state.phone,
          message:this.state.message}
        }).then( res => {
          if (res.data.msg === 'success'){
            alert("Email sent, awesome!");
          this.resetForm()
        }else if(res.data.msg === 'fail'){
          alert("oops, somthing went wrong. Try agian")
        }         
      })

    }
    resetForm = () => {
      this.setState({
          name: '',
          company:'',
          email: '',
          phone:'',
          message: '' 
      })
  }
    render(){
    return(
      <div className="container">
      <p style={{fontSize:'25px'}}>Get in Touch</p>
      <h2 style={{color:'red',fontSize:'16px'}}>"Please feel free to contact me and I will get back to you soon!"</h2>   

          <Form horizontal onSubmit={this.handleSubmit} method="POST" action="send">
          <FormGroup>
            <Row componentClass={ControlLabel} sm={2}>
            <ControlLabel>Name:</ControlLabel>  
            <FormControl type="text" className="input" value={this.state.name} onChange={this.onNameChange}/>
            </Row>
          </FormGroup>
          <FormGroup>
            <Row componentClass={ControlLabel} sm={2}>
            <ControlLabel>Company:</ControlLabel>
            <FormControl type="text" className="input" value={this.state.company} onChange={this.onCompanyChange}/>
            </Row>
          </FormGroup>
          <FormGroup>
            <Row componentClass={ControlLabel} sm={2}>
            <ControlLabel>Email:</ControlLabel>
            <FormControl type="email" value={this.state.email} onChange={this.onEmailChange}/>
            </Row>
          </FormGroup>
          <FormGroup>
            <Row componentClass={ControlLabel} sm={2}>
            <ControlLabel>Phone:</ControlLabel>
            <FormControl type="number" value={this.state.phone} onChange={this.onPhoneChange}/>
            </Row>
          </FormGroup>
            <FormGroup>
              <Row componentClass={ControlLabel} sm={2}>
              <ControlLabel>Message:</ControlLabel>
              <FormControl componentClass="textarea" rows="4" vlaue={this.state.message} onChange={this.onMessageChange}/>
              </Row>
          </FormGroup>

          <Button type="submit" className="button">{this.state.buttonText}</Button>

          </Form>
      </div>


    );    
}
}
export default FormApp;
