import React,{Component} from 'react';
import './FormApp';
import axios from 'axios';


class FormApp extends Component{
    constructor(props){
        super(props);
        this.state={
            name:'',
            company:'',
            email:'',
            phone:'',
            message:'',
            sent: false,
            buttonText:'send Message'
        };
        this.onNameChange = this.onNameChange.bind(this);
        this.onCompanyChange=this.onCompanyChange.bind(this);
        this.onEmailChange = this.onEmailChange.bind(this);
        this.onPhoneCange=this.onPhoneChange.bind(this);
        this.onMessageChange = this.onMessageChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
    }
    onNameChange(event){
      this.setState({name:event.target.value})
      }
    onCompanyChange(event){
        this.setState({company:event.target.value})
        }
    onEmailChange(event){
         this.setState({email: event.target.value})
       }
       onPhoneChange(event){
        this.setState({phone:event.target.value})
        }
       onMessageChange(event){
          this.setState({message: event.target.value})
    }
     handleSubmit(event) {
        event.preventDefault();

      axios({
        methode:"POST",
        url:'http://localhost:3000/send',
        data:{
          name:this.state.name,
          company:this.state.company,
          email:this.state.email,
          phone:this.state.phone,
          message:this.state.message}
        }).then( res => {
          if (res.data.msg === 'success'){
            alert("Email sent, awesome!");
          this.resetForm()
        }else if(res.data.msg === 'fail'){
          alert("oops, somthing went wrong. Try agian")
        }         
      })  
    }
    resetForm = () => {
      this.setState({
          name: '',
          company:'',
          email: '',
          phone:'',
          message: '' 
      })
  }
    render(){
    return(
      <div className="contanier">
      <div className="wrapper">
      <div className="company-info">
       <ul>
          <li><i className="fa fa-road"></i> 44 Something st</li>
          <li><i className="fa fa-phone"></i> (555) 555-5555</li>
          <li><i className="fa fa-envelope"></i> test@acme.test</li>
        </ul>
        </div>
      <div className="contact">
      <h3>Email Me</h3>
      <form method="POST" action="send"> 
        <p>
            <label>Name</label>
            <input type="text" name="name"></input>
          </p>
          <p>
            <label>Company</label>
            <input type="text" name="company"></input>
          </p>
          <p>
            <label>Email Address</label>
            <input type="email" name="email"></input>
          </p>
          <p>
            <label>Phone Number</label>
            <input type="text" name="phone"></input>
          </p>
          <p className="full">
            <label>Message</label>
            <textarea name="message" rows="5"></textarea>
          </p>
          <p className="full">
            <button type="submit" >Submit</button>
          </p>

      </form>

      </div>
      </div>
      </div>




    );    
}
}
export default FormApp;
